    $args = array(
        'post_type' => 'post',
        'posts_per_page' => '-1',
        'order' => 'ASC',
        'orderby' => 'title',
        );                                              
    $the_query = new WP_Query( $args );
    
    // The Loop
    if ( $the_query->have_posts() ) {
        echo '<ul>';
        $count = 0;
        while ( $the_query->have_posts() ) {
            $the_query->the_post();
            $thumbnail = get_the_post_thumbnail_url(get_the_ID());
            $post_slug = get_post_field( 'post_name', get_post() );
            set_post_thumbnail( get_the_ID(), $attachment_id[$count] );
            echo '';
            echo '<li><a href="'.$thumbnail.'">' . $post_slug .','.$thumbnail.'</li></a>';
            echo '';
            $count++;
        }
        echo '</ul>';
        /* Restore original Post Data */
        wp_reset_postdata();
    } else {
        // no posts found
    }
